mapper.select:
SELECT id, first_name AS firstName, last_name AS lastName, initial, color, password, email
FROM users
WHERE id = #{userId}

SELECT id, first_name, last_name, email
FROM users
WHERE email LIKE #{likeSearchWord};

SELECT id, first_name AS firstName, last_name AS lastName, color
FROM users
WHERE email = #{email}
  AND password = #{password};
